        return new Source("{% extends 'base.html.twig' %}

{% block title %}Список задач{% endblock %}

{% block body %}
    <h1>Список задач</h1>

    <table class=\"table\">
        <thead>
            <tr>
                <th>Id</th>
                <th>Название</th>
                <th>Описание</th>
                <th>Дата создания</th>
                <th>Действия</th>
            </tr>
        </thead>
        <tbody>
        {% for task in tasks %}
            <tr>
                <td>{{ task.id }}</td>
                <td>{{ task.title }}</td>
                <td>{{ task.description }}</td>
                <td>{{ task.createdAt ? task.createdAt|date('Y-m-d H:i') : '' }}</td>
                <td>
                    <a href=\"{{ path('app_task_show', {'id': task.id}) }}\">Просмотр</a>
                    <a href=\"{{ path('app_task_edit', {'id': task.id}) }}\">Редактировать</a>
                </td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"5\">Задачи не найдены</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>

    <a href=\"{{ path('app_task_new') }}\">Создать новую задачу</a>
{% endblock %}", "task/index.html.twig", "D:\\git\\php_symphony\\todo-project\\templates\\task\\index.html.twig");
